uilder: (context, index) {          return Table(children: rows);        },        itemCount: 1,      );    }  }  getListItems() {    List<Widget> listRows = [];    for (int i = 0; i < accountTypelist.length; i++) {      var accountData = accountTypelist[i];      listRows.add(Container(          height: Constants_data.getHeight(context, 74),          child: InkWell(              onTap: () async {                Map<String, dynamic> arg = new HashMap();                arg["account_type"] = accountData["account_type"];                arg["menu_title"] = accountData["menu_title"];                await Navigator.of(context)                    .pushNamed(accountData["screen"], arguments: accountData["account_type"] != '' ? arg : '');                this.setState(() {                  isBadgeUpdated = false;                });              },              child: Stack(                clipBehavior: Clip.none, children: [                  Card(                      shape: RoundedRectangleBorder(                        borderRadius: BorderRadius.circular(10.0),                      ),                      elevation: 5,                      margin: EdgeInsets.symmetric(horizontal: 15, vertical: 7),                      child: Row(children: [                        Expanded(                          flex: 30,                          child: Container(                              padding: EdgeInsets.all(Constants_data.getFontSize(context, 12)),                              child: Image.asset(                                "assets/images/" + accountData["menu_icon"],                                //color: AppColors.black_color87,                              )),                        ),                        Expanded(                            flex: 90,                            child: Text(                              accountData["menu_title"],                              maxLines: 1,                              style: T